/* eslint-disable react/forbid-prop-types */
const React = require('react');
const PropTypes = require('prop-types');
const BaseFooter = require('../components/base-footer');
const DownloadAppBanner = require('../components/download-app-banner');
const UserMenu = require('../components/user-menu');
const FooterNavigation = require('../components/footer-navigation');

const FooterMobile = (props) => {
  const {
    downloadAppBanner,
    userMenu,
    tertiaries,
    footerNavigation,
    ...baseFooterProps
  } = props;

  return (
    <BaseFooter
      {...baseFooterProps}
      banner={<DownloadAppBanner {...downloadAppBanner} />}
      userMenuSection={<UserMenu {...userMenu} />}
      tertiary={tertiaries}
    >
      <FooterNavigation {...footerNavigation} />
    </BaseFooter>
  );
};

FooterMobile.propTypes = {
  userMenu: PropTypes.object.isRequired,
  downloadAppBanner: PropTypes.object.isRequired,
  tertiaries: PropTypes.oneOfType([
    PropTypes.arrayOf(PropTypes.node),
    PropTypes.node,
  ]),
};

FooterMobile.defaultProps = {
  tertiaries: null,
};

module.exports = FooterMobile;
