const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const FooterPlus = require('.');

describe('Mercado Libre Navigation', () => {
  describe('Footer Plus', () => {
    it('matches the default snapshot for Footer in mobile', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <FooterPlus
          footerNavigation={{
            links: [{
              href: 'https://www.mercadolibre.com.ar/jobs',
              name: 'Trabaja con nosotros',
              id: 'jobs',
            }],
          }}
          downloadAppBanner={{
            link: 'https://www.mercadolibre.com.ar',
            label: 'Comprá y vendé en la app',
          }}
          footerLinks={{
            links: [{
              href: 'https://www.mercadolibre.com.ar/jobs',
              name: 'Trabaja con nosotros',
              id: 'jobs',
            }],
          }}
          userMenu={{
            links: [{
              href: 'https://www.mercadolibre.com.ar/registration',
              name: 'Creá tu cuenta',
              id: 'registration',
            }],
            isUserLoggedIn: false,
          }}
        />
      );

      const renderedComponent = renderer.render(component);

      expect(renderedComponent).toMatchSnapshot();
      expect(renderedComponent.type.name).toEqual('FooterMobile');
    });

    it('matches the default snapshot for Footer in desktop', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <FooterPlus
          isDesktop
          footerNavigation={{
            links: [{
              href: 'https://www.mercadolibre.com.ar/jobs',
              name: 'Trabaja con nosotros',
              id: 'jobs',
            }],
          }}
          downloadAppBanner={{
            link: 'https://www.mercadolibre.com.ar',
            label: 'Comprá y vendé en la app',
          }}
          footerLinks={{
            links: [{
              href: 'https://www.mercadolibre.com.ar/jobs',
              name: 'Trabaja con nosotros',
              id: 'jobs',
            }],
          }}
          userMenu={{
            links: [{
              href: 'https://www.mercadolibre.com.ar/registration',
              name: 'Creá tu cuenta',
              id: 'registration',
            }],
            isUserLoggedIn: false,
          }}
        />
      );

      const renderedComponent = renderer.render(component);

      expect(renderedComponent).toMatchSnapshot();
      expect(renderedComponent.type.name).toEqual('FooterDesktop');
    });
  });
});
