const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const FooterMobile = require('./mobile');

describe('Mercado Libre Navigation', () => {
  describe('Footer Mobile', () => {
    const downloadApp = {
      'header-top': [{
        type: 'html',
        typeData: {
          content: '<div>headerTop</div>',
        },
      }],
      'footer-top': [{
        type: 'html',
        typeData: {
          content: '<div>footerTop</div>',
        },
      }],
      'footer-bottom': [{
        type: 'html',
        typeData: {
          content: '<div>footerBottom</div>',
        },
      }],
    };

    it('matches the default snapshot for Footer Mobile', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <FooterMobile
          footerNavigation={{
            links: [{
              href: 'https://www.mercadolibre.com.ar/jobs',
              name: 'Trabaja con nosotros',
              id: 'jobs',
            }],
          }}
          copyright={{
            since: '1999',
            companyName: 'Mercado Libre',
            currentYear: 2019,
          }}
          footerLinks={{
            links: [{
              href: 'https://www.mercadolibre.com.ar/jobs',
              name: 'Trabaja con nosotros',
              id: 'jobs',
            }],
          }}
          downloadAppBanner={{
            link: 'https://www.mercadolibre.com.ar',
            label: 'Comprá y vendé en la app',
          }}
          downloadApp={downloadApp}
          userMenu={{
            links: [{
              href: 'https://www.mercadolibre.com.ar/registration',
              name: 'Creá tu cuenta',
              id: 'registration',
            }],
            isUserLoggedIn: false,
          }}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
    it('matches snapshot for Footer MPE with icon in mobile', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <FooterMobile
          footerNavigation={{
            links: [{
              href: 'https://www.mercadolibre.com.ar/jobs',
              name: 'Trabaja con nosotros',
              id: 'jobs',
            }],
          }}
          copyright={{
            since: '1999',
            companyName: 'Mercado Libre',
            currentYear: 2019,
          }}
          footerLinks={{
            links: [{
              href: 'https://www.mercadolibre.com.ar/jobs',
              name: 'Trabaja con nosotros',
              id: 'jobs',
            }],
            icons: {
              helpClaims: {
                image: '/help-icon.png',
                image2x: '/help-icon@2x.png',
                width: '24',
                height: '12',
              },
            },
          }}
          downloadAppBanner={{
            link: 'https://www.mercadolibre.com.ar',
            label: 'Comprá y vendé en la app',
            target: '_blank',
          }}
          downloadApp={downloadApp}
          userMenu={{
            links: [{
              href: 'https://www.mercadolibre.com.ar/registration',
              name: 'Creá tu cuenta',
              id: 'registration',
            }],
            isUserLoggedIn: false,
          }}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
