const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const CPMobile = require('./cp.mobile');

const baseProps = {
  label: 'Enviar a Capital Federal',
  linkCP: {
    href: 'https://www.mercadolibre.com.ar/navigation/addresses-hub?go=https%3A%2F%2Fdesa.mercadolibre.com.ar%3A8443%2F',
    className: 'nav-cp',
  },
  name: 'Enviar a Capital Federal',
};

describe('Mercado Libre Navigation', () => {
  describe('CP Mobile Component', () => {
    it('matches the default snapshot for CP mobile component', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <CPMobile
          {...baseProps}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
