const React = require('react');
const PropTypes = require('prop-types');
const tabIndexes = require('../../../utils/tab-index');

const namespace = 'exhibitor';
const Exhibitor = (props) => {
  const { link, title, picture } = props;
  if (!picture) {
    return null;
  }

  return (
    <a
      href={link}
      className={`${namespace}__picture`}
      tabIndex={tabIndexes('exhibitor')}
    >
      { /* eslint-disable-next-line nordic/no-img-tags */}
      <img
        src={picture}
        title={title}
        alt={title}
      />
    </a>
  );
};

Exhibitor.propTypes = {
  link: PropTypes.string,
  title: PropTypes.string,
  picture: PropTypes.string,
};

Exhibitor.defaultProps = {
  picture: null,
  title: null,
  link: null,
};

module.exports = Exhibitor;
