const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const Exhibitor = require('.');

describe('Mercado Libre Navigation', () => {
  describe('Exhibitor Component', () => {
    it('matches the default snapshot for exhibitor component', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Exhibitor
          link="https://www.mercadolibre.com.ar/exhibitor"
          title="exhibitor"
          picture="https://www.mercadolibre.com.ar/exhibitor.jpeg"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('renders nothing since picture prop is null', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Exhibitor
          picture={null}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
