const cdnReplace = require('../../../utils/cdn-replace');

const exhibitorProps = (props) => {
  const { exhibitorInfo, cdn } = props;

  if (!exhibitorInfo) {
    return {};
  }

  const { permalink, title, picture } = exhibitorInfo;

  return {
    link: permalink,
    title,
    picture: cdnReplace(picture, cdn.name),
  };
};

module.exports = exhibitorProps;
