const React = require('react');
const PropTypes = require('prop-types');

const namespace = 'hamburger';
const HamburguerMenu = (props) => {
  const { navMenuSwitchId } = props;

  // TODO: add tab index and review their order.
  /* eslint-disable jsx-a11y/label-has-associated-control */
  return (
    <label htmlFor={navMenuSwitchId} aria-controls="nav-header-menu">
      <span className={`${namespace}-top-bread`} />
      <span className={`${namespace}-patty`} />
      <span className={`${namespace}-bottom-bread`} />
    </label>
  );
};

HamburguerMenu.propTypes = {
  navMenuSwitchId: PropTypes.string.isRequired,
};

module.exports = HamburguerMenu;
