/* eslint-disable react/no-danger */
const React = require('react');
const PropTypes = require('prop-types');

const bannerText = 'kyc-active-campaign';
const KycMessage = (props) => {
  const { text, cta } = props;

  return text && (
    <a className={`${bannerText}__nav-header`} href={cta.link}>
      <div className={`${bannerText}__message`}>
        <div className={`${bannerText}__bounds`}>
          <p className={`${bannerText}__text`}>{text}</p>
          <span className={`${bannerText}__cta`}>{cta.text}</span>
        </div>
      </div>
    </a>
  );
};

KycMessage.propTypes = {
  text: PropTypes.string,
  cta: PropTypes.shape({
    link: PropTypes.string,
    text: PropTypes.string,
  }),
};

KycMessage.defaultProps = {
  text: '',
  cta: {},
};

module.exports = KycMessage;
