const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const KycMessage = require('.');

describe('Mercado Libre Navigation', () => {
  describe('KYC Message Widget', () => {
    it('should matches an empty snapshot since there is not text', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <KycMessage
          text={null}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('should matches the default snapshot', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <KycMessage
          text="Completá los datos de tu cuenta para poder seguir usándola."
          cta={{ text: 'Empezar', link: 'https://www.mercadolibre.com.ar/kyc?initiative=supply-communications&landing=true&congrats=true&callback=https%3A%2F%2Fwww.mercadolibre.com.ar%2Fventas%2Flistado' }}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});

