const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const Logo = require('.');

describe('Mercado Libre Navigation', () => {
  describe('Logo Component', () => {
    it('matches the default snapshot for logo', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Logo url="https://www.mercadolibre.com.ar" label="Mercado Libre" />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the snapshot for logo with melilab enabled', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Logo
          url="https://www.mercadolibre.com.ar"
          label="Mercado Libre"
          melilab={{
            melilab: 'z',
            labUrl: 'https://www.mercadolibre.com.ar/gz/labs',
            isEnabled: true,
          }}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
