const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const MeliLab = require('.');

describe('Mercado Libre Navigation', () => {
  describe('Meli Lab Component', () => {
    it('matches the default snapshot for melilab, showing styles for mobile', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <MeliLab
          melilab="z"
          labUrl="https://www.mercadolibre.com.ar/gz/labs"
          isMobile
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for melilab, showing styles for desktop', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <MeliLab
          melilab="z"
          labUrl="https://www.mercadolibre.com.ar/gz/labs"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
