const isMobile = require('../../../utils/is-mobile');

const melilabProps = (props) => {
  const { urls, melilab } = props;
  const labUrl = `${urls.baseDomain.link}/gz/labs`;

  return {
    labUrl,
    isEnabled: !!melilab,
    melilab,
    isMobile: isMobile(props),
  };
};

module.exports = melilabProps;

