const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const Searchbox = require('.');

describe('Mercado Libre Navigation', () => {
  describe('Searchbox Component', () => {
    it('matches the default snapshot for searchbox component', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Searchbox
          isDesktop
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
    it('matches the snapshot for searchbox mobile component', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Searchbox
          isDesktop={false}
          placeholderMobile="Buscar"
          clearTitle="Limpiar"
          closeTitle="Cerrar"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
    it('matches the snapshot for searchbox with category mobile', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Searchbox
          isDesktop={false}
          placeholderMobile="Buscar"
          clearTitle="Limpiar"
          closeTitle="Cerrar"
          category={{
            name: 'Solo en computación',
            permalink: 'https://computacion.mercadolibre.com.co/$query',
            id: 'OC:MLA1234',
          }}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
    it('matches the snapshot for searchbox with category desktop', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Searchbox
          isDesktop
          placeholderMobile="Buscar"
          clearTitle="Limpiar"
          closeTitle="Cerrar"
          category={{
            name: 'Solo en computación',
            permalink: 'https://computacion.mercadolibre.com.co/$query',
            id: 'OC:MLA1234',
          }}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
