/* eslint-disable max-len */
const React = require('react');
const PropTypes = require('prop-types');
const Style = require('frontend-style');
const { SUPERMARKET_LOGO } = require('../../../icons/ids');

const SupermarketLogoIcon = require('../../../icons/supermarket-logo');

const renderIcon = ({ id, ...props }) => {
  const className = 'nav-category-icon';
  switch (id) {
    case SUPERMARKET_LOGO:
      return <SupermarketLogoIcon className={className} {...props} />;
    default:
      return null;
  }
};

// Head's UP! Here we are using the minified versions of our css files
// We need them to be inline, that's why we want to minify them so save as much bundle size as possible
// If you want to make changes to them, please see file ./styles-category.css
// apply changes there, minify them and then paste them here.
const Category = ({ id, name, icon, defaultChecked }) => [
  <p id="mlCategory" className="nav-category" title={name}>
    <input
      type="checkbox"
      id="categorySearch"
      name="as_categ_id"
      value={id}
      checked={defaultChecked}
    />
    <label htmlFor="categorySearch">
      {name}
      {icon && renderIcon(icon)}
    </label>
  </p>,
  <Style renderChildrenInPlace={false}>
    {'.nav-search .nav-category{background-color:#fff;color:#666;line-height:20px;height:24px;margin:0;white-space:nowrap;overflow:hidden;text-overflow:ellipsis;max-width:55%;padding:2px 10px;position:absolute;right:50px;top:15px;text-align:right}.nav-search .nav-category:hover .nav-label-small{width:initial}.nav-search .nav-category label{-webkit-user-select:none}.nav-search .nav-category .nav-label-small{display:inline-block;text-overflow:ellipsis;overflow:hidden;white-space:nowrap;vertical-align:middle;width:58px}.nav-search .nav-category input[type=checkbox]{border:1px solid #ccc;background:0;box-shadow:none;display:inline-block;margin:2px 9px 0 0;height:14px;padding:0;vertical-align:top;width:14px;-webkit-border-radius:3px;-moz-border-radius:3px;border-radius:3px}.nav-search .nav-category input[type=checkbox]:focus{outline:1px dotted #9a9a9a}.mobile-view#mlCategory{top:unset;max-width:unset;text-align:left}.nav-search .nav-category input[type=checkbox]{vertical-align:unset}@media (max-width:768px){.nav-search .nav-category{display:none}}.nav-search .nav-category label .nav-category-icon{vertical-align:text-bottom}.nav-search .nav-category label .nav-icon-supermarket-logo{width:115px;height:18px}.mobile-view#mlCategory label .nav-icon-supermarket-logo{width:125px;height:22px;margin-left:1px;vertical-align:middle}'}
  </Style>,
];

Category.propTypes = {
  id: PropTypes.string.isRequired,
  name: PropTypes.string.isRequired,
  defaultChecked: PropTypes.bool,
};

Category.defaultProps = {
  defaultChecked: false,
};

module.exports = Category;
