const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const Category = require('./searchbox-category');

describe('Mercado Libre Navigation', () => {
  describe('Searchbox Category  Component', () => {
    it('matches the default snapshot for category component without icon', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Category
          id="mlCategory"
          name="Juguetes"
          defaultChecked={false}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for category component with icon', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Category
          id="mlCategory"
          name="Solo en "
          icon={{ id: 'supermarket_logo' }}
          defaultChecked={false}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for category with defaultChecked component', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Category
          id="mlCategory"
          name="Juguetes"
          defaultChecked
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
