const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const SkipToMainContent = require('.');

describe('Mercado Libre Navigation', () => {
  describe('Skip to main content Component', () => {
    it('matches the default snapshot skip to main content', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <SkipToMainContent
          text="Ir al contenido principal"
          mainContent="#root"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
