const { getValue } = require('ml-navigation-config');

const DEFAULT = 'Ir al contenido principal';

const skipToMainContentProps = (props) => {
  const { mainContent, platform, req, searchFocus } = props;

  const skipToMainContent = getValue({
    key: 'skipToMainContent',
    req,
    platform,
  });

  return {
    mainContent,
    text: skipToMainContent ? skipToMainContent.text : DEFAULT,
    searchFocus,
  };
};

module.exports = skipToMainContentProps;
