const React = require('react');
const PropTypes = require('prop-types');
const GuestMenu = require('./guest');
const LoggedInMenu = require('./logged-in');

const DesktopUserMenu = (props) => {
  const { isUserLoggedIn, guestMenu, loggedInMenu } = props;

  return (
    <nav id="nav-header-menu">
      {
        isUserLoggedIn
          ? <LoggedInMenu {...loggedInMenu} />
          : <GuestMenu {...guestMenu} />
      }
    </nav>
  );
};

DesktopUserMenu.propTypes = {
  isUserLoggedIn: PropTypes.bool.isRequired,
  guestMenu: PropTypes.object,
  loggedInMenu: PropTypes.object,
};

DesktopUserMenu.defaultProps = {
  guestMenu: {},
  loggedInMenu: {},
};

module.exports = DesktopUserMenu;
