const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const Avatar = require('.');

const baseProps = {
  hasPicture: false,
  hasLoyalty: false,
  lazyLoad: 'off',
  alt: 'Test',
};

describe('Mercado Libre Navigation', () => {
  describe('Avatar Component', () => {
    it('matches the default snapshot for Avatar component', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Avatar
          {...baseProps}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for Avatar component with', () => {
      const renderer = new ShallowRenderer();
      const props = { ...baseProps,
        hasPicture: true,
        lowDensityPictureUrl: 'https://www.mercadolibre.com.ar/user/picture.jpg',
        highDensityPictureUrl: 'https://www.mercadolibre.com.ar/user/picture.jpg' };
      const component = (
        <Avatar
          {...props}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
