const React = require('react');
const PropTypes = require('prop-types');
const UserLink = require('../../../../nav-link');

const FallbackMenu = (props) => {
  const { links } = props;
  return (
    <nav className="nav-header-user-layer">
      { /* eslint-disable-next-line react/no-array-index-key */ }
      { links.map((link, index) => <UserLink key={`userlink-key-${index}`} {...link} />)}
    </nav>
  );
};

FallbackMenu.propTypes = {
  links: PropTypes.array.isRequired,
};

module.exports = FallbackMenu;
