const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const LoggedInMenu = require('.');

const userLinksProps = {
  links: [
    {
      link: {
        href: 'https://www.mercadolibre.com.ar',
      },
      name: 'Inicio',
      icon: 'nav-home-icon',
      selected: false,
      id: 'home',
    },
    {
      link: {
        href: 'https://www.mercadolibre.com.ar/ofertas',
      },
      name: 'Ofertas',
      icon: 'nav-deals-icon',
      selected: false,
      id: 'deals',
    },
  ],
};

describe('Mercado Libre Navigation', () => {
  describe('Logged In Menu Component', () => {
    it('matches the default snapshot for user menu, in desktop for logged in user', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <LoggedInMenu
          welcome={{
            show: true,
            showAvatar: true,
            avatar: {},
            type: 'plus',
            nickname: 'Test',
            fallbackMenu: {
              links: [{
                link: {
                  href: 'https://www.mercadolibre.com.ar',
                },
                name: 'Inicio',
                icon: 'nav-home-icon',
                selected: false,
                id: 'home',
              },
              {
                link: {
                  href: 'https://www.mercadolibre.com.ar/ofertas',
                },
                name: 'Ofertas',
                icon: 'nav-deals-icon',
                selected: false,
                id: 'deals',
              }],
            },
          }}
          userLinks={userLinksProps}
          userWidget={{
            baseUrl: 'https://ml.static.com',
            version: '1.0.0',
            domain: 'www.mercadolibre.com.ar',
            show: true,
          }}
        />
      );

      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
