const React = require('react');
const { node, arrayOf, oneOfType, elementType } = require('prop-types');

const nameSpace = 'andes-visually-hidden';

const VisuallyHidden = ({ children, component: Component, ...other }) => (
  <Component className={nameSpace} {...other}>
    {children}
  </Component>
);
module.exports = VisuallyHidden;

VisuallyHidden.propTypes = {
  children: oneOfType([node, arrayOf(node)]).isRequired,
  component: elementType,
};

VisuallyHidden.defaultProps = {
  component: 'span',
};
