const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const DownloadAppLinkSideEffect = require('./side-effect');

describe('Mercado Libre Navigation', () => {
  describe('Download App Link Side Effect Component', () => {
    it('matches the default snapshot for Download App Link Side Effect component', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <DownloadAppLinkSideEffect
          os="android"
          application="frontend-navigations"
          link={{
            id: 'nav-header',
          }}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
