const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const GuestWelcome = require('.');

const props = {
  title: 'Bienvenido!',
  subtitle: 'Ingresa o registrate',
  login: {
    link: 'https://www.mercadolibre.com.ar/login',
    name: 'Ingresa',
  },
  registration: {
    link: 'https://www.mercadolibre.com.ar/registration',
    name: 'Crea tu cuenta',
  },
};

describe('Mercado Libre Navigation', () => {
  describe('Guest User Menu Welcome Component', () => {
    it('matches the default snapshot for GuestWelcome component', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <GuestWelcome
          {...props}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
