const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const LoyaltyLevelIcon = require('./icon');

describe('Mercado Libre Navigation', () => {
  describe('LoyaltyLevelIcon Component', () => {
    it('matches the default snapshot for LoyaltyLevelIcon component', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <LoyaltyLevelIcon
          namespace="nav-header-loyalty-level"
          className="nav-header-loyalty-level"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
