const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const Loyalty = require('.');

const baseProps = {
  hasLoyalty: true,
  level: 4,
  href: 'https://www.mercadolibre.com.ar',
  levelName: 'Aficionado',
  email: 'test@test.com',
};

describe('Mercado Libre Navigation', () => {
  describe('Loyalty Mobile Component', () => {
    it('matches the default snapshot for Loyalty component', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Loyalty
          {...baseProps}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for Loyalty component with no loyalty', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Loyalty
          {...baseProps}
          hasLoyalty={false}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
