const notificationsProps = require('../../../notifications/props');
const Notification = require('../../../notifications');

const id = 'notificationsCenter';

const notificationsEnhancement = (url, props) => {
  const notificationsEnhancementProps = notificationsProps(props);

  const newUrl = { ...url,
    Aside: Notification,
    ...notificationsEnhancementProps };

  return newUrl;
};

module.exports = {
  id,
  enhance: notificationsEnhancement,
};
