// eslint-disable-next-line max-len
const notificationsLink = require('./enhancement');
const Notifications = require('../../../notifications');

describe('Mercado Libre Navigation', () => {
  describe('Notification Link Enhancement', () => {
    it('returns a valid enhancement for notification link', () => {
      const url = {
        link: {
          href: 'https://www.mercadolibre.com.ar',
        },
        name: 'Home',
      };
      const props = {
        notificationsInfo: {
          count: 0,
        },
      };

      const urlWithEnhancedProps = notificationsLink.enhance(url, props);

      expect(urlWithEnhancedProps.link.href).toEqual(url.link.href);
      expect(urlWithEnhancedProps.name).toEqual(url.name);
      expect(urlWithEnhancedProps.Aside).toEqual(Notifications);
    });
  });
});
