/* eslint-disable nordic/i18n-jsx-no-literals */
const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const Script = require('frontend-script');
const UserLink = require('.');

describe('Mercado Libre Navigation', () => {
  describe('User Link Component', () => {
    it('matches the default snapshot for user link', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <UserLink
          link={{
            href: 'https://www.mercadolibre.com.ar',
          }}
          name="Inicio"
          icon="nav-home-icon"
        />
      );

      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for user link with a Side Effect and Aside', () => {
      const renderer = new ShallowRenderer();
      const Aside = () => <div />;

      const component = (
        <UserLink
          link={{
            href: 'https://www.mercadolibre.com.ar',
          }}
          name="Inicio"
          icon="nav-home-icon"
          SideEffect={() => <Script>var test = 0;</Script>}
          Aside={Aside}
        />
      );

      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
