const React = require('react');
const PropTypes = require('prop-types');
const Header = require('../components/base-header');
const Internal = require('../components/internal-header');
const { types } = require('../../../layout-type-detection');

const HeaderInternal = (props) => {
  const { internal } = props;
  const internalType = types.INTERNAL;

  return (
    <Header
      type={internalType}
      {...props}
      showNotifications={false}
      showUserMenu={false}
      showLogo={false}
    >
      <Internal {...internal} />
    </Header>
  );
};

HeaderInternal.propTypes = {
  // eslint-disable-next-line react/forbid-prop-types
  internal: PropTypes.object,
};

HeaderInternal.defaultProps = {
  internal: {
    title: null,
    btnHref: null,
    btnText: null,
  },
};

module.exports = HeaderInternal;
