const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const Header = require('.');

const baseProps = {
  type: 'plus',
  siteId: 'MLA',
  logo: { url: 'https://www.mercadolibre.com.ar', label: 'Mercado Libre' },
  navMenuSwitchId: 'nav-header-menu-switch',
  melilab: {
    isEnabled: false,
  },
  isMobile: true,
  isDesktop: false,
  userMenu: {
    isUserLoggedIn: true,
  },
  cart: {
    cartInfo: { badge_icon: { quantity: 0 }, location: { type: 'address', value: '1014518121' } },
    show: true,
    isMobile: true,
    url: 'https://www.mercadolibre.com.ar/gz/cart',
    title: 'Carrito',
    cdnDomain: 'https://http2.mlstatic.com',
  },
  notifications: {
    count: 0,
  },
  cp: {
    show: true,
    isDesktop: false,
    selector: '[data-js=cp]',
    application: 'frontend-navigations',
  },
  searchbox: {
    category: null,
    isMobile: false,
    isDesktop: false,
    closeTitle: 'Cerrar',
    clearTitle: 'Limpiar',
    placeholderMobile: '',
    placeholder: '',
    widget: null,
  },
  navMenu: {
    show: true,
    links: [],
  },
  skipToMainContent: {
    text: 'Ir a contenido principal',
  },
  downloadApp: {
    'footer-top': [{
      type: 'html',
      typeData: {
        content: '<div>footerTop</div>',
      },
    }],
    'header-top': [{
      type: 'html',
      typeData: {
        content: '<div>headerTop</div>',
      },
    }],
    'header-bottom': [{
      type: 'html',
      typeData: {
        content: '<div>headerBottom</div>',
      },
    }],
  },
};

describe('Mercado Libre Navigation', () => {
  describe('Plus Component', () => {
    it('matches the default snapshot for plus header', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Header
          {...baseProps}
          type="plus"
          siteId="MLA"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for plus header in desktop', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Header
          {...baseProps}
          type="plus"
          siteId="MLA"
          isMobile={false}
          isDesktop
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for plus header but no cart is shown', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Header
          {...baseProps}
          type="plus"
          siteId="MLA"
          cart={({ ...baseProps.cart, show: false })}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
