const { APPLICATION } = require('frontend-env');

const preloadedStateProps = (props) => {
  const { device, platform } = props;
  const os = device.osName;

  return {
    application: APPLICATION || 'ml-web-navigation',
    os,
    siteId: platform.siteId,
    domain: platform.domain,
  };
};

module.exports = preloadedStateProps;
