const cdnReplace = require('./cdn-replace');

describe('Mercado Libre Navigation', () => {
  describe('CDN Replace', () => {
    it('successfully replaces url', () => {
      const replace = cdnReplace('https://http2.mlstatic.com/image.jpeg', 'akamaiQuic');

      expect(replace).toEqual('https://mlstaticquic-a.akamaihd.net/image.jpeg');
    });

    it('fails to replace since url is not defined', () => {
      const replace = cdnReplace(null, 'akamaiQuic');

      expect(replace).toEqual(null);
    });

    it('fails to replace since cdn provided is not defined', () => {
      const replace = cdnReplace(null, 'cdn-that-does-not-exists');

      expect(replace).toEqual(null);
    });
  });
});

