const { getValue } = require('ml-navigation-config');

const injectIconsPerPlatform = (props) => {
  const { platform, req } = props;

  const iconsPerPlatform = getValue({
    key: 'mobileHeaderMenuIcons',
    req,
    platform,
  });
  props.iconsPerPlatform = iconsPerPlatform;

  return props;
};

module.exports = {
  inject: injectIconsPerPlatform,
};
