const isDesktop = require('./is-desktop');

describe('Mercado Libre Navigation', () => {
  describe('Is Desktop', () => {
    it('returns true since we are in desktop', () => {
      expect(isDesktop({
        device: {
          desktop: true,
        },
      })).toEqual(true);
    });

    it('returns false since we are in mobile', () => {
      expect(isDesktop({
        device: {
          mobile: true,
        },
      })).toEqual(false);
    });
  });
});

