const links = require('@frontend-navigations/commons/links');

const injectDefaultIcons = (options) => {
  const { icons, originalProps } = options;

  if (!icons) {
    options.icons = originalProps.icons || originalProps.iconsPerPlatform;
  }

  return options;
};

module.exports = {
  ...links,
  generateLink: options => links.generateLink(injectDefaultIcons(options)),
  mapLinksToUrls: options => links.mapLinksToUrls(injectDefaultIcons(options)),
};
