/* eslint-disable security/detect-object-injection */
const { mapLinksToUrls } = require('./links');

/**
 * Creates an array of arrays that represents each section in the guest user menu, with each link formatted
 * @param {array} links - array of objects what links we want to use
 * example: [{link: 'locationCP', }]
*/
const mapNavMenuLinksToUrls = ({ links, ...rest }) => {
  const urls = links.map(({ link }) => link);

  return mapLinksToUrls({
    links: urls,
    ...rest,
    linkConfigs: links,
  });
};

module.exports = mapNavMenuLinksToUrls;
