const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const Categories = require('.');

describe('Mercado Libre Navigation', () => {
  describe('Categories Widget', () => {
    it('matches the default snapshot for categories widget', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Categories baseUrl="https://ml.static.com" version="1.0.0" domain="www.mercadolibre.com.ar" />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for categories widget, but defaults to version', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Categories baseUrl="https://ml.static.com" domain="www.mercadolibre.com.ar" />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});

