/* eslint-disable nordic/i18n-jsx-no-literals */
const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const DownloadAppHtml = require('./html');

describe('Mercado Libre Download App - raw HTML', () => {
  it.only('should render raw html string', () => {
    const renderer = new ShallowRenderer();
    const props = {
      data: {
        content: '<script>console.log(\'test 1\')</script>',
      },
    };
    const component = <DownloadAppHtml {...props} />;
    expect(renderer.render(component)).toMatchSnapshot();
  });
});
