/* eslint-disable nordic/i18n-jsx-no-literals */
const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const DownloadAppScript = require('./script');

describe('Mercado Libre Download App - Script components', () => {
  it('should render script components without callbacks', () => {
    const renderer = new ShallowRenderer();
    const props = {
      data: {
        src: 'some-source-1.js',
        on: 'load',
        priority: 1,
        preload: false,
      },
    };
    const component = <DownloadAppScript {...props} />;
    expect(renderer.render(component)).toMatchSnapshot();
  });

  it('should render script components with callbacks', () => {
    const renderer = new ShallowRenderer();
    const props = {
      data: {
        src: 'some-source-2.js',
        on: 'now',
        'callback-content': 'console.log(\'some callback content 2\')',
        priority: 2,
        preload: true,
      },
    };
    const component = <DownloadAppScript {...props} />;
    expect(renderer.render(component)).toMatchSnapshot();
  });
});
