const React = require('react');
const PropTypes = require('prop-types');
const Script = require('frontend-script');

const ModalWidget = ({
  baseUrl,
  version,
}) => {
  const url = `${baseUrl}/frontend-assets/nav-widget-ml-modal-iframe/v${version}/modal.js`;
  return <Script src={url} />;
};

ModalWidget.propTypes = {
  baseUrl: PropTypes.string.isRequired,
  version: PropTypes.string,
};

ModalWidget.defaultProps = {
  version: '1.0.6',
};

module.exports = ModalWidget;
