const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const ModalWidget = require('.');

describe('Mercado Libre Navigation', () => {
  describe('Modal widget Component', () => {
    it('matches the default snapshot for modal widget component', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <ModalWidget
          baseUrl="https://http2.mlsatatic.com"
          version="3.0.0"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
    it('matches the snapshot for modal widget without version component', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <ModalWidget
          baseUrl="https://http2.mlsatatic.com"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
