const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const ModelessBox = require('.');

describe('Mercado Libre Navigation', () => {
  describe('ModelessBox Widget', () => {
    it('matches an empty snapshot since there are no callbacks', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <ModelessBox
          baseUrl="https://ml.static.com"
          version="1.0.0"
          mainDomain="www.mercadolibre.com.ar"
          callbacks={['some', 'callbacks']}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for ModelessBox widget, but defaults to version', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <ModelessBox
          baseUrl="https://ml.static.com"
          mainDomain="www.mercadolibre.com.ar"
          callbacks={['some', 'callbacks']}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});

