const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const OnboardingCPWidget = require('.');

describe('Mercado Libre Navigation', () => {
  describe('OnboardingCP Component', () => {
    it('matches the default snapshot for onboarding cp widget component', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <OnboardingCPWidget
          show
          baseUrl="https://http2.mlstatic.com"
          cpInfo={{ inferred: true, zipcode: '1234' }}
          domain="mercadolibre.com.ar"
          siteId="MLA"
          locale="es_AR"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for onboarding cp widget component with show false', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <OnboardingCPWidget
          show={false}
          baseUrl="https://http2.mlstatic.com"
          cpInfo={{ inferred: true, zipcode: '1234' }}
          domain="mercadolibre.com.ar"
          siteId="MLA"
          locale="es_AR"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
