const { isPlus } = require('@frontend-navigations/commons/layout-type-detection');
const countryConfig = require('frontend-country_config');

const onboardingProps = (props) => {
  const { navigationFeatures, cdn, cpInfo, platform, currentLocation, type } = props;

  return {
    show: !!(isPlus(type) && currentLocation && currentLocation.showOnboarding),
    version: navigationFeatures.onBoardingCPVersion,
    baseUrl: cdn.link,
    cpInfo,
    domain: platform.domain,
    siteId: platform.siteId,
    locale: countryConfig(platform.countryId).locale,
  };
};

module.exports = onboardingProps;
