const isUserLoggedIn = require('@frontend-navigations/commons/is-user-logged-in');

const searchboxProps = (props) => {
  const {
    navigationFeatures,
    cdn,
    platform,
    urls,
    navigationUrls,
    category,
    device,
  } = props;

  return {
    version: navigationFeatures.searchBoxVersion,
    baseUrl: cdn.link,
    platformId: platform.id,
    siteId: platform.siteId,
    searchEndpoint: `${navigationUrls.listing.link}/$query`,
    categoryEndpoint: category && category.permalink ? category.permalink : null,
    isUserLoggedIn: isUserLoggedIn(props),
    siteDomain: urls.mainDomain.link,
    deviceType: device.type,
  };
};

module.exports = searchboxProps;
