const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const SignUpMessage = require('.');

describe('Mercado Libre Navigation', () => {
  describe('SignUpMessage Widget', () => {
    it('matches an empty snapshot since show = false', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <SignUpMessage
          baseUrl="https://ml.static.com"
          show={false}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <SignUpMessage
          baseUrl="https://ml.static.com"
          show
          isUserCompany
          userName="Test"
          userEmail="test@email.com"
          userNickname="nickname"
          changeNicknameUrl="/change/nickname"
          companyFAQUrl="/faq/url"
          siteId="MLA"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});

