const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const UserMenu = require('.');

describe('Mercado Libre Navigation', () => {
  describe('UserMenu Widget', () => {
    it('matches the default snapshot for UserMenu widget', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <UserMenu show baseUrl="https://ml.static.com" version="1.0.0" domain="www.mercadolibre.com.ar" />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for UserMenu widget, but defaults to version', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <UserMenu show baseUrl="https://ml.static.com" domain="www.mercadolibre.com.ar" />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for UserMenu widget, when widget is not visible', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <UserMenu show={false} baseUrl="https://ml.static.com" domain="www.mercadolibre.com.ar" />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});

