const isDesktop = require('../../utils/is-desktop');
const { isPlus, isLite } = require('../../../layout-type-detection');

const userWidgetProps = (props) => {
  const { navigationFeatures, cdn, urls, type, userInfo } = props;

  return {
    version: navigationFeatures.userMenuVersion,
    domain: urls.mainDomain.link,
    baseUrl: cdn.link,
    show: isDesktop(props) && (isPlus(type) || isLite(type)),
    userInfo,
  };
};

module.exports = userWidgetProps;
